// File: Pierre1.java// Author: Seth Roller// Date: 3/27/18// Purpose: To perform multiplication between fractionsimport java.util.*;  // Scannerimport java.io.*;    // PrintStreampublic class Pierre1 extends Object c{  public static void main(String args[])  {    PrintStream theScreen = new PrintStream(System.out);    Scanner theKeyboard = new Scanner(System.in);    theScreen.println("\nTo convert fractional measures,");    theScreen.print(" enter the fractional measure you want to convert: ");    Fraction oldMeasure = new Fraction();    oldMeasure.read(theKeyboard);    theScreen.println();    theScreen.print("\nEnter the fractional amount to "		    + "reduce/increase it by: ");        Fraction scaleFactor = new Fraction(1,6);    scaleFactor.read(theKeyboard);    Fraction newMeasure = oldMeasure.times(scaleFactor);    theScreen.println();    theScreen.print("\nThe converted measurement is: ");    theScreen.print(newMeasure);    theScreen.println();    theScreen.println();	}}