//  Prepend2.java // Author: Seth Roller// Date: 4/17/18// Purpose: compares the time required to prepend 1 values at the end of// the listsimport java.util.*;  //ArrayList and LinkedListimport java.io.*;    // PrintStreampublic class Prepend2{  static PrintStream theScreen = new PrintStream(System.out);  static Scanner theKeyboard = new Scanner(System.in);    public static void main(String args[])    {		HOJTimer theTimer = new HOJTimer();	// create a HOJTimer			theScreen.print("\nPrepend Timing Test 2:\n"       			+ " Enter the size of the list to prepend to: ");	int n = theKeyboard.nextInt();	// Linked List Based List	                                                    	LinkedList<Integer> aLinkedList = null;	//declare it		aLinkedList = new LinkedList<Integer>();	// build empty linked list	for (int i = 0; i < n; i++)	    aLinkedList.add(0, new Integer(i));	// prepend n values	//  to list	theTimer.start();	// start HOJTimer	aLinkedList.add(0, new Integer(1));	//  add one value to list	theTimer.stop();	// stop HOJTimer			theScreen.println("\nPrepending one value to a LinkedList took : "			  + theTimer.getTime() + " seconds.");					// Array Based List  	ArrayList<Integer> anArrayList = null;	//declare it	anArrayList = new ArrayList<Integer>(n);	// build empty list	for (int i = 0; i < n; i++)	    anArrayList.add(0, new Integer(i));	// prepend n values	//  to list	theTimer.start();	// start HOJTimer	anArrayList.add(0, new Integer(1));	//  add one value to list	theTimer.stop();	// stop HOJTimer					theScreen.println("\nPrepending one value to an ArrayList took : "			  + theTimer.getTime() + " seconds.");	theScreen.println();    }    }//end of class